export default {
    name: 'AwChartProgress',

    props: {
        percent: {
            type: Number,
            required: true
        },

        emptyColor: {
            type: String,
            default: '#E8E9EB'
        },

        fillColor: {
            type: String,
            default: '#594FCF'
        }
    },

    computed: {
        filledIndex() {
            const p = this.percent > 100 ? 100 : this.percent
            return Math.round(20 * (p / 100))
        },

        emptySections() {
            return sections.slice(this.filledIndex).join('')
        },

        filledSections() {
            return sections.slice(0, this.filledIndex).join('')
        }
    }
}
